mapper.select:
SELECT * FROM (SELECT A1.*,ROWNUM RN FROM ( SELECT D2.JFID,D2.YHM,D2.MC,D2.DZ,D2.YDDH,D2.YX,D2.ZPLJ FROM DDJ02 D2,DDJ02_DDJ01 D1 WHERE D2.JFID=D1.DDJ02_JFID
AND MC LIKE '%'||#{MC}||'%'
ORDER BY LRRQ DESC ) A1 WHERE ROWNUM <=#{max}) WHERE RN>=#{min}

SELECT
JFID,YHM,MC,YDDH,YX,BZ,LRRY_MC,LRRQ,XZQH,XZQH
XZQHMC,DZ,XB,SR,XL,JTRK,RJSR,YHLB,SCMJ FROM DDJ02 WHERE JFID=#{0}

mapper.update:
UPDATE DDJ02
MC=#{MC}, XZQH=#{XZQH}, DZ=#{DZ}, YDDH=#{YDDH}, YHLB=#{YHLB}, XB=#{XB}, SR=#{SR}, SR=null, XL=#{XL}, JTRK=#{JTRK}, RJSR=#{RJSR}, YX=#{YX}, SCMJ=#{SCMJ}, BZ=#{BZ},
WHERE JFID=#{JFID}

UPDATE DDJ02
DLRQ=#{DLRQ}, TOKEN=#{TOKEN},
WHERE JFID=#{JFID}

mapper.select:
SELECT * FROM (SELECT A1.*,ROWNUM RN FROM ( SELECT DISTINCT D2.JFID,D2.YHM,D2.MC,D2.DZ,D2.YDDH,D2.SCMJ,D2.ZPLJ,D2.ZT FROM DDJ02 D2,DDJ02_DDJ01 D2_D1 WHERE D2_D1.DDJ02_JFID=D2.JFID
AND D2.MC LIKE '%'||#{MC}||'%'
AND D2.XZQH LIKE '%'||#{XZQH}||'%'
AND D2_D1.DDJ01_JFID=#{DDJ01_JFID}
ORDER BY D2.MC ) A1 WHERE ROWNUM <=#{MAX}) WHERE RN>=#{MIN}

SELECT * FROM (SELECT A1.*,ROWNUM RN FROM ( SELECT DISTINCT D2.JFID,D2.YHM,D2.MC,D2.DZ,D2.YDDH,D2.YX,D2.ZPLJ FROM DDJ02 D2,DDJ02_DDJ01 D2_D1 WHERE D2.JFID=D2_D1.DDJ02_JFID
